import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import "./App.css";

import Header from "./components/Header/header";
import Footer from "./components/Footer/footer";

import HomePage from "./pages/HomePage/HomePage";
import LoginPage from "./pages/LoginPage/loginPage";
import ProfilePage from "./pages/ProfilePage/profilePage";
import ErrorPage from "./pages/ErrorPage/errorPage";

// Fonction pour vérifier si l'utilisateur est connecté
const isAuthenticated = () => {
  const token = localStorage.getItem('token');
  return !!token; // Convertit la valeur en booléen
};

// Fonction pour rediriger vers la page de connexion si l'utilisateur n'est pas connecté
const PrivateRoute = ({ element, ...rest }) => {
  return isAuthenticated() ? (
    element
  ) : (
    <Navigate to="/login" replace />
  );
};

export default function App() {
  return (
    <BrowserRouter>
      <Header />
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/login" element={<LoginPage />} />
        {/* Utilisez PrivateRoute pour les pages nécessitant une connexion */}
        <PrivateRoute path="/profile" element={<ProfilePage />} />
        <Route path="*" element={<ErrorPage />} />
      </Routes>
      <Footer />
    </BrowserRouter>
  );
}
