import React, { useState } from 'react';
import {useSelector, useDispatch } from "react-redux";
import { updateUserProfile } from '../redux/actions/userActions';

import './editName.css'; 

function EditName() {
  const dispatch = useDispatch();
  const [editMode, setEditMode] = useState(false);
  const { firstName, userName, lastName } = useSelector((state) => state.user);

  const [newUserName, setNewUserName] = useState(userName); // Utilise un état local pour stocker le nouveau nom d'utilisateur


  const handleUserNameChange = (event) => {
    setUserName(event.target.value);
  };  
  const [ setUserName] = useState('');


  const handleCancel = () => {
    setEditMode(false);
    setNewUserName(userName)
    // Optionnel: réinitialiser le nom à la dernière valeur sauvegardée si nécessaire
  };

  const handleSave = () => {
    dispatch(updateUserProfile({ userName: newUserName })); // Dispatche l'action pour mettre à jour le nom d'utilisateur dans le store Redux
    setEditMode(false);
  };



  return (
    <div className="tete">

      {editMode ? (
        <>
          <h1>Edit user info<br /></h1>
          <div className='input-group'>
            <label htmlFor="userName">User name:</label>
            <input type="text" value={userName} onChange={handleUserNameChange} />
          </div>

          <div className='input-group'>
            <label htmlFor="firstName">First name:</label>
            <input type="text" value={firstName} disabled />
          </div>

          <div className='input-group'>
            <label htmlFor="lastName">Last name:</label>
            <input label='LastName' type="text" value={lastName} disabled />
          </div>

          <div className='button-edit'>
            <button className='edit-button-info' onClick={handleSave}>Save</button>
            <button className='edit-button-info' onClick={handleCancel}>Cancel</button>
          </div>

        </>
      ) : (
        <>
          <h1>Welcome back<br /></h1>
          <h2>{firstName} {lastName}</h2>
          <button className="edit-button" onClick={() => setEditMode(true)}>Edit Name</button>
        </>
      )}
    </div>
  );
}

export default EditName;
