import React from 'react';
import { useNavigate } from 'react-router-dom'; // Importez Redirect depuis react-router-dom
import { useSelector } from 'react-redux'; // Importez useSelector depuis react-redux

import Transaction from '../../components/Transaction/transaction';
import EditName from '../../components/EditName/editName';
import TransactionData from '../../data/transactionData.json';
import './profilePage.css';

function ProfilePage() {
  // Obtenez l'état de connexion à partir du Redux store
  const { isConnected } = useSelector(state => state.login);
  const navigate = useNavigate(); // Initialisez useHistory

  // Vérifiez si l'utilisateur est connecté
  if (!isConnected) {
    // Redirigez l'utilisateur vers la page de connexion s'il n'est pas connecté
    navigate ("/login");
    return 
  }

  return (
    <main className="main bg-dark">
      {isConnected ? (
        <>
        <div>
        <EditName/>
        {TransactionData.map((data) => (
            /* Return item component */
            <Transaction 
              key={data.id}
              title={data.title}
              amount={data.amount}
              description={data.description}
            />
        ))}
      </div>
        </>

      ) : (
        <>
          <h1>Veuillez vous connecter pour accéder à cette page.</h1>; // Affichez un message d'invitation à la connexion

        </>
      )}

    </main>
  );
}

export default ProfilePage;
