import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom'; 
import { login, userProfile } from '../../redux/actions/all.actions';
import './connect.css';

function Connect() {
  const dispatch = useDispatch();
  const navigate = useNavigate(); 
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });


  const { username, password } = formData;

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await dispatch(login(username, password));
      await dispatch(userProfile()); 
      navigate('/profil'); 
    } catch  {
      function handleResponse(response) {
        const errorMessage = document.getElementById('error-message');
        if (response && response.error) {
          // S'il y a une erreur dans la réponse
          errorMessage.classList.remove('hidden'); // Affiche le message d'erreur
          errorMessage.classList.add('visible'); // Ajoute la classe visible
        } else {
          // S'il n'y a pas d'erreur dans la réponse
          errorMessage.classList.remove('visible'); // Masque le message d'erreur
          errorMessage.classList.add('hidden'); // Ajoute la classe hidden
        }
      }
      handleResponse()
    }
  };



  return (
    <main className="main bg-dark">
  <section className="sign-in-content">
    <i className="fa fa-user-circle sign-in-icon"></i>
    <h1>Sign In</h1>
    {/* Affiche le message d'erreur s'il y en a un */}
    <p className="error-message">problemme</p>
    <form className="form" onSubmit={handleSubmit}>
      <div className="input-wrapper">
        <label htmlFor="username">Username</label>
        <input
          type="text"
          id="username"
          autoComplete="current-username"
          value={username}
          onChange={handleChange}
        />
      </div>
      <div className="input-wrapper">
        <label htmlFor="password">Password</label>
        <input
          type="password"
          id="password"
          autoComplete="current-password"
          value={password}
          onChange={handleChange}
        />
      </div>
      <div className="input-remember">
        <input type="checkbox" id="remember-me" />
        <label htmlFor="remember-me">Remember me</label>
      </div>
      <button type="submit" className="sign-in-button">
        Sign In
      </button>
    </form>
  </section>
</main>

  );
}

export default Connect;
