import React, { useEffect } from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';
import './index.css';

import reportWebVitals from './reportWebVitals';

// Redux
import { Provider } from 'react-redux';
import store from './redux/Store/index';
import { login, userProfile } from './redux/actions/all.actions';
import { useNavigate } from 'react-router-dom'; // Importez useNavigate depuis react-router-dom

function Index() {
  const navigate = useNavigate();

  useEffect(() => {
    const connectInstantly = async () => {
      // Démarrer l'action userProfile dès le début de l'application
      store.dispatch(userProfile());

      // Vérifier si le token existe dans le localStorage
      const token = localStorage.getItem('token');
      if (token) {
        // Si le token existe, déclencher l'action de connexion avec ce token
        store.dispatch(login());
        // Rediriger vers la page du profil une fois la connexion établie
        navigate('/profil');
      }
    };

    connectInstantly();
  }, [navigate]); // Assurez-vous de mettre navigate comme dépendance pour éviter les avertissements des Hooks

  return (
    <React.StrictMode>
      <Provider store={store}>
        <App />
      </Provider>
    </React.StrictMode>
  );
}

// Rendu de l'application
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<Index />);

// Si vous souhaitez mesurer les performances de votre application, vous pouvez utiliser reportWebVitals
reportWebVitals();
